import React, { useEffect, useState } from 'react';
import Axios from 'axios';

function Mainscreen() {

    const [data, setData] = useState([]);
    const [optionValue, setOptionValue] = useState("");

    // useEffect(() => {
    //     getData();
    // })

    // To Fetch Data from API

    const getData = (make_name) => {
        Axios.get(`https://vpic.nhtsa.dot.gov/api/vehicles/getmodelsformake/${make_name}?format=json`).then((response) => {
            console.log(response.data.Results);
            setData(response.data.Results);
        });
    }

    // Set Dropdown 
    const handleChange = (e) => {
        e.preventDefault();
        console.log(e.target.value);
        setOptionValue(e.target.value);
    }

    // To remove common Brand Name From Drop Down 
    const newFilteredArray = data.reduce((unique, o) => {
        if(!unique.some(obj => obj.Make_ID === o.Make_ID && obj.Make_Name === o.Make_Name))
        {
            unique.push(o);
        }
        return unique;
    },[])
    console.log("Filtered Array"+JSON.stringify(newFilteredArray));


    return (
        <div>
            Hello From Main_screen
            <form>
                <label>Select Car Makes: </label>
                <select onClick={getData}>

                        {/* <option value="BMW">BMW</option>
                        <option value="Tata">Tata</option>
                        <option value="Volvo">Volvo</option> */}

                        {data.map((name) => {
                            return (
                                <option value={name.Make_Name}>{name.Make_Name}</option>
                            );
                        })}
                </select>
            </form>

            {/* <button onClick={getData}>GetData</button> */}


            <div>
                {newFilteredArray.map((makeName) => {
                    return (
                        <ol>
                            <li>
                                <h3> ID: {makeName.Make_ID}</h3>
                                <h3> Make Name: {makeName.Make_Name}</h3>
                                <h3> Model Name: {makeName.Model_Name}</h3>
                            </li>
                        </ol>
                    );
                })}
            </div>
        </div>
    )
}

export default Mainscreen
