import React, { useEffect, useState } from 'react';
import Axios from 'axios';

function Mainscreen() {

    const [data, setData] = useState([]);
    const [optionValue, setOptionValue] = useState();

    useEffect((make_name) => {
        Axios.get(`https://vpic.nhtsa.dot.gov/api/vehicles/getmodelsformake/${make_name}?format=json`).then((response) => {
            console.log(response.data.Results);
            setData(response.data.Results);
        });
    })

    const getData = (make_name) => {
        Axios.get(`https://vpic.nhtsa.dot.gov/api/vehicles/getmodelsformake/${make_name}?format=json`).then((response) => {
            console.log(response.data.Results);
            setData(response.data.Results);
        });
    }

    const handleChange = (e) => {
        e.preventDefault();
        console.log(e.target.value);
    }


    return (
        <div>
            Hello From Main_screen
            <form>
                <label>Select Car Makes: </label>
                <select onChange={handleChange}>
                    {data.map((makeName) => {
                        return (
                
                            <option value={makeName.Make_Name}>{makeName.Make_Name}</option>
                       

                        );
                })}
                </select>

                
            </form>

            <button onClick={getData}>GetData</button>


            <div>
                {data.map((makeName) => {
                    return (
                        // console.log(makeName.Make_ID),
                        // console.log(makeName.Make_Name),
                        // console.log(makeName.Model_Name)
                        <ol>
                            <li>
                                <h3> ID: {makeName.Make_ID}</h3>
                                <h3> Make Name: {makeName.Make_Name}</h3>
                                <h3> Model Name: {makeName.Model_Name}</h3>
                            </li>
                        </ol>,

                        <select>
                            <option value={makeName.Make_Name}>{makeName.Make_Name}</option>
                        </select>
                        
                    );
                })}
            </div>
        </div>
    )
}

export default Mainscreen
