import React, { useEffect, useState } from 'react';
import Axios from 'axios';

function Mainscreen() {

    const [data, setData] = useState([]);
    const [makerName, setMakername] = useState("");

    const handleChange = (e) => {
        e.preventDefault();
        Axios.get("https://vpic.nhtsa.dot.gov/api/vehicles/getmodelsformake/{make-name}?format=json").then((response) => {
            setData(response.data.Results);
            console.log(response.data.Results);
        })
        console.log(e.target.value);
        setMakername(e.target.value);
    }

    return (
        <div>
            Hello From Main_screen
            <form>
                <label>Select Car Makes: </label>
                <select onChange={handleChange}>

                    {data.map((makerName) => {
                        <option value={makerName.Make_Name}>{makerName.Make_Name}</option>

                    })}
                        {/* <option value="BMW">BMW</option>
                        <option value="Tata">Tata</option>
                        <option value="Volvo">Volvo</option> */}
                </select>


            </form>
        </div>
    )
}

export default Mainscreen
